INSERT INTO `product` (`id`, `title`, `sum`, `content`, `meta_keyword`, `meta_description`, `e_title`, `e_sum`, `e_content`, `e_meta_keyword`, `e_meta_description`, `pId`, `maps`, `city`, `district`, `img`, `active`, `home`, `ind`) VALUES
(1, 'Làm Thế Nào Để Có Một Làn Da Khỏe', 'Làm Thế Nào Để Có Một Làn Da Khỏe', '<h2 style="text-align:center"><span style="font-size:16px"><strong>Sau đây là 9 câu hỏi làm đẹp phổ biến và câu trả lời chính xác dành cho mỗi câu hỏi.</strong></span></h2>\r\n\r\n<div style="margin: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-stretch: inherit; font-size: medium; line-height: inherit; font-family: Arial, Helvetica, sans-serif; vertical-align: baseline; color: rgb(0, 0, 0); text-align: center;">&nbsp;</div>\r\n\r\n<div class="knc-content" style="margin: 0px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-stretch: inherit; font-size: 17px; line-height: 25px; font-family: ">\r\n<div style="margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; box-sizing: border-box; text-align: center;">\r\n<div style="margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; box-sizing: border-box;"><img alt="01-80eb9" src="http://kenh14cdn.com/thumb_w/600/d9baecf2c8/2015/09/08/01-80eb9.jpg" style="border:0px; box-sizing:border-box; font-family:inherit; font-size:inherit; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:480px; line-height:inherit; margin:0px; padding:0px; vertical-align:baseline; width:800px" /></div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div style="margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; box-sizing: border-box; text-align: left;"><span style="font-family:inherit; font-size:inherit; line-height:inherit">Câu trả lời:</span>&nbsp;Việc make up nói chung và sử dụng kem nền nói riêng chỉ gây mụn vì 2 lý do: một là sản phẩm bạn sử dụng gây tắc lỗ chân lông, hai là bạn không tẩy trang kỹ. Bởi vậy, khi bạn sử dụng sản phẩm an toàn và làm sạch da thật kỹ, da bạn hoàn toàn không cần &quot;nghỉ chơi&quot; với việc trang điểm để &quot;thở&quot; và trở nên đẹp hơn (thực chất, khái niệm &quot;thở&quot; không tồn tại với làn da).</div>\r\n\r\n<div style="margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; box-sizing: border-box; text-align: left;">Khi mua bất cứ sản phẩm nào dùng để thoa lên da mặt, từ kem dưỡng đến kem nền, bạn hãy chắc chắn nó không gây tắc, bí lỗ chân lông (hãy tìm chữ &quot;noncomedogenic&quot; trên bao bì). Trong trường hợp da bạn bị mụn, hãy chọn kem nền có thành phần trị mụn như acid salicylic để có thể vừa che phủ lại vừa trị mụn cho da.</div>\r\n\r\n<div style="margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; box-sizing: border-box; text-align: left;">Việc tẩy trang thật kỹ cũng là chìa khóa để da không bị xấu đi khi trang điểm. Vào cuối ngày, hãy luôn nhớ làm sạch da với hai sản phẩm: dầu tẩy trang và sữa rửa mặt nhẹ dịu.&nbsp;</div>\r\n\r\n<div style="margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; box-sizing: border-box; text-align: left;">Một khi bạn đã tuân thủ hai nguyên tắc kể trên, bạn sẽ thấy rằng thực sự không tồn tại khái niệm &quot;trang điểm nhiều làm da xấu đi&quot;.</div>\r\n\r\n<div style="margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; box-sizing: border-box;">&nbsp;</div>\r\n</div>\r\n\r\n<div style="margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; box-sizing: border-box; text-align: center;">\r\n<div style="margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; box-sizing: border-box;"><img alt="02-80eb9" src="http://kenh14cdn.com/thumb_w/600/d9baecf2c8/2015/09/08/02-80eb9.jpg" style="border:0px; box-sizing:border-box; font-family:inherit; font-size:inherit; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:480px; line-height:inherit; margin:0px; padding:0px; vertical-align:baseline; width:800px" /></div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div style="margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; box-sizing: border-box; text-align: left;"><span style="font-family:inherit; font-size:inherit; line-height:inherit">Câu trả lời:</span>&nbsp;Mụn đầu đen hình thành do bã nhờn tích tụ trong lỗ chân lông và bị ôxy hóa nên mới có màu đen. Cách trị mụn đầu đen hiệu quả nhất là sử dụng sản phẩm trị mụn có chứa benzoyl peroxide để diệt khuẩn và loại bỏ bã nhờn. Kèm theo đó, bạn cần tẩy da chết thường xuyên để ngăn chặn mụn đầu đen hình thành thêm.&nbsp;</div>\r\n\r\n<div style="margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; box-sizing: border-box;">&nbsp;</div>\r\n\r\n<div style="margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; box-sizing: border-box;">\r\n<div style="margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; box-sizing: border-box;"><img alt="03-80eb9" src="http://kenh14cdn.com/thumb_w/600/d9baecf2c8/2015/09/08/03-80eb9.jpg" style="border:0px; box-sizing:border-box; font-family:inherit; font-size:inherit; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:480px; line-height:inherit; margin:0px; padding:0px; vertical-align:baseline; width:800px" /></div>\r\n\r\n<div style="margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; box-sizing: border-box;">&nbsp;</div>\r\n\r\n<div style="margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; box-sizing: border-box; text-align: left;"><span style="font-family:inherit; font-size:inherit; line-height:inherit">Câu trả lời:&nbsp;</span>Khi mới tập tành make up, bạn không cần thiết phải sắm sửa ngay đủ bộ mỹ phẩm như các chuyên gia trang điểm chuyên nghiệp<span style="font-family:inherit; font-size:inherit; line-height:inherit">&nbsp;</span>mà chỉ cần những sản phẩm thiết yếu sau đây: kem nền (BB cream, phấn cushion hoặc tinted moisturizer, v.v...), phấn phủ không màu, một màu son tôn da (bạn có thể chọn sản phẩm môi và má 2 trong 1 để vừa làm son, vừa làm má hồng rất tiện), một cây eyeliner (dạng nước, gel hoặc chì), kẹp mi và một cây mascara. Sau này, khi đã sử dụng thành thạo những sản phẩm cơ bản trên, bạn có thể mua thêm những món đồ phụ trợ để make up chuyên nghiệp hơn, như sản phẩm tạo khối, tạo sáng, v.v...</div>\r\n\r\n<div style="margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; box-sizing: border-box;">&nbsp;</div>\r\n\r\n<div style="margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; box-sizing: border-box;">\r\n<div style="margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; box-sizing: border-box;"><img alt="04-80eb9" src="http://kenh14cdn.com/thumb_w/600/d9baecf2c8/2015/09/08/04-80eb9.jpg" style="border:0px; box-sizing:border-box; font-family:inherit; font-size:inherit; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:480px; line-height:inherit; margin:0px; padding:0px; vertical-align:baseline; width:800px" /></div>\r\n\r\n<div style="margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; box-sizing: border-box;">&nbsp;</div>\r\n\r\n<div style="margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; box-sizing: border-box; text-align: left;"><span style="font-family:inherit; font-size:inherit; line-height:inherit">Câu trả lời:</span>&nbsp;Đáng tiếc là không. Về cơ bản, lỗ chân lông không thể đóng, mở hay thu nhỏ lại được. Lỗ chân lông to hay nhỏ phụ thuộc vào gene của mỗi người và bạn không thể thay đổi kích cỡ của chúng. Tuy không thể thu nhỏ nhưng lỗ chân lông lại có thể bị giãn ra khi bị tắc nghẽn. Điều đó có nghĩa là lỗ chân lông càng sạch sẽ bao nhiêu, bạn sẽ càng cảm thấy chúng nhỏ bấy nhiêu và đó là lý do tại sao chúng ta nên làm sạch da thật kỹ. Cách làm sạch từng lỗ chân lông hiệu quả là rửa mặt bằng thao tác xoáy tròn tập trung vào từng vùng trên mặt.</div>\r\n\r\n<div style="margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; box-sizing: border-box;">&nbsp;</div>\r\n\r\n<div style="margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; box-sizing: border-box;">\r\n<div style="margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; box-sizing: border-box;"><img alt="05-80eb9" src="http://kenh14cdn.com/thumb_w/600/d9baecf2c8/2015/09/08/05-80eb9.jpg" style="border:0px; box-sizing:border-box; font-family:inherit; font-size:inherit; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:480px; line-height:inherit; margin:0px; padding:0px; vertical-align:baseline; width:800px" /></div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div style="margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; box-sizing: border-box; text-align: left;"><span style="font-family:inherit; font-size:inherit; line-height:inherit">Câu trả lời:</span>&nbsp;Lâu nay, uống nhiều nước (ít nhất là 2,5 lít/ngày) vẫn được tin là một trong những cách hiệu quả nhất giúp cung cấp độ ẩm cho da, từ đó giúp da khỏe đẹp. Tuy nhiên, trên thực tế lại không có bất cứ nghiên cứu khoa học nào chứng minh rằng việc uống nhiều nước có thể giúp da đẹp hơn hay làm cơ thể thon gọn hơn. &quot;Lượng nước mà chúng ta uống vào không có tác dụng cung cấp độ ẩm cho da bởi làn da của chúng ta hoạt động như một tấm rào chắn vững chắc giúp ngăn cách môi trường bên trong và ngoài cơ thể. Chỉ trong trường hợp bạn bị thiếu nước một cách trầm trọng, làn da mới bị ảnh hưởng. Những vấn đề hay gặp với làn da khô sẽ không tự hết chỉ nhờ việc uống 8 cốc nước mỗi ngày mà cần đến những sản phẩm dưỡng da phù hợp&quot; - chuyên gia sức khỏe Andrew Carroll viết trên tờ New York Times cách đây không lâu. Ngoài ra, đúng là cơ thể chúng ta cần 2,5 lít nước/ngày để duy trì sự khỏe mạnh nhưng mỗi người không cần thiết phải uống đúng 2,5 lít nước bởi nước cũng có thể được bổ sung qua đường ăn, khi phần lớn các loại thức ăn đều có chứa một lượng nước nhất định.</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<div style="margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; box-sizing: border-box; text-align: center;">\r\n<div style="margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; box-sizing: border-box;">&nbsp;</div>\r\n</div>\r\n\r\n<div style="margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; box-sizing: border-box; text-align: center;">\r\n<div style="margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; box-sizing: border-box;"><img alt="07-80eb9" src="http://kenh14cdn.com/thumb_w/600/d9baecf2c8/2015/09/08/07-80eb9.jpg" style="border:0px; box-sizing:border-box; font-family:inherit; font-size:inherit; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:480px; line-height:inherit; margin:0px; padding:0px; vertical-align:baseline; width:800px" /></div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div style="margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; box-sizing: border-box; text-align: left;"><span style="font-family:inherit; font-size:inherit; line-height:inherit">Câu trả lời:</span>&nbsp;Da mặt bạn sẽ khó thấm kem dưỡng, khó &quot;ăn&quot; kem nền khi tế bào chết bị ứ đọng trên da mà không được dọn dẹp. Việc bạn cần làm là tẩy da chết thật thường xuyên với tần suất từ 2 - 3 lần một tuần. Khi đó, da sẽ mỏng, mịn hơn hẳn và đóng vai trò như một lớp phông nền mịn mượt giúp các loại mỹ phẩm &quot;bám dính&quot; lên da một cách trơn tru, dễ dàng. Ngoài ra, việc dùng kem lót (primer) trước khi đánh nền cũng sẽ khiến lớp nền bám lên da chắc hơn và lâu trôi hơn hẳn so với bình thường.</div>\r\n</div>\r\n\r\n<div style="margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; box-sizing: border-box; text-align: center;">&nbsp;</div>\r\n\r\n<div style="margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; box-sizing: border-box; text-align: center;">\r\n<div style="margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; box-sizing: border-box;"><img alt="08-80eb9" src="http://kenh14cdn.com/thumb_w/600/d9baecf2c8/2015/09/08/08-80eb9.jpg" style="border:0px; box-sizing:border-box; font-family:inherit; font-size:inherit; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:480px; line-height:inherit; margin:0px; padding:0px; vertical-align:baseline; width:800px" /></div>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<div style="margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; box-sizing: border-box; text-align: left;"><span style="font-family:inherit; font-size:inherit; line-height:inherit">Câu trả lời:</span>&nbsp;Khi môi bạn bị thâm và lấn át hết màu son, bí quyết là hãy tán kem che khuyết điểm lên môi trước khi tô son. Ngoài ra, bạn hãy chọn loại son có kết cấu màu thật đặc (opaque) - thường là son lì, son nước (còn gọi là son kem hay liquid lipstick). Đặc điểm của những loại son này là khả năng lên màu rất mạnh, có thể che phủ hoàn toàn màu môi nguyên thủy của bạn. Bên cạnh đó, hãy thường xuyên tẩy da chết cho môi và thường xuyên thoa mật ong để cải thiện phần nào sắc tố môi.</div>\r\n\r\n<div style="margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; box-sizing: border-box;">&nbsp;</div>\r\n\r\n<div style="margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; box-sizing: border-box;">\r\n<div style="margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; box-sizing: border-box;"><img alt="09-80eb9" src="http://kenh14cdn.com/thumb_w/600/d9baecf2c8/2015/09/08/09-80eb9.jpg" style="border:0px; box-sizing:border-box; font-family:inherit; font-size:inherit; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:480px; line-height:inherit; margin:0px; padding:0px; vertical-align:baseline; width:800px" /></div>\r\n</div>\r\n</div>\r\n\r\n<div><span style="font-family:inherit; font-size:inherit; line-height:inherit">Câu trả lời:</span>&nbsp;Tần suất gội đầu phụ thuộc vào mái tóc cũng như môi trường sống của riêng mỗi người. Trong trường hợp bạn sống ở nơi nhiều khói bụi, tần suất gội đầu nên là 1 - 2 ngày một lần. Với dầu gội nhẹ dịu có thành phần dưỡng tóc, bạn hoàn toàn có thể gội đầu hằng ngày mà không sợ tóc bị khô hay xấu đi. Khi tóc bạn thuộc dạng tóc khô hoặc bạn sống ở nơi có khí hậu trong lành, tần suất gội đầu có thể giãn xuống còn khoảng 2 - 3 ngày một lần.</div>\r\n\r\n<div style="margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; box-sizing: border-box;">\r\n<div style="margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; box-sizing: border-box; text-align: center;">&nbsp;</div>\r\n\r\n<div style="margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; box-sizing: border-box; text-align: center;">\r\n<div style="margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; box-sizing: border-box;">\r\n<div style="margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; box-sizing: border-box;"><img alt="06-80eb9" src="http://kenh14cdn.com/thumb_w/600/d9baecf2c8/2015/09/08/06-80eb9.jpg" style="border:0px; box-sizing:border-box; font-family:inherit; font-size:inherit; font-stretch:inherit; font-style:inherit; font-variant:inherit; font-weight:inherit; height:480px; line-height:inherit; margin:0px; padding:0px; vertical-align:baseline; width:800px" /></div>\r\n</div>\r\n\r\n<div style="margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; box-sizing: border-box; text-align: left;"><span style="font-family:inherit; font-size:inherit; line-height:inherit">Câu trả lời:</span>&nbsp;Việc cắt tóc thường xuyên không hề tác động gì đến chân tóc, bởi vậy, có thể khẳng định rằng cắt tóc không hề khiến tóc mọc nhanh hơn. Trên thực tế, tóc bạn vẫn mọc với tốc độ bình thường và nếu bạn có cảm thấy tóc &quot;mọc nhanh hơn&quot; thì đó cũng chỉ là cảm giác chủ quan của bạn mà thôi. Mục đích thực sự của việc tỉa ngắn đuôi tóc thường xuyên là để ngăn ngừa đuôi tóc chẻ ngọn.</div>\r\n\r\n<div style="margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; box-sizing: border-box; text-align: right;">Nguồn: Elly</div>\r\n</div>\r\n</div>\r\n</div>\r\n', 'Làm Thế Nào Để Có Một Làn Da Khỏe', 'Làm Thế Nào Để Có Một Làn Da Khỏe', '', '', '', '', '', 1, '', 0, 0, '148473223601-80eb9.jpg', 1, 0, 1),
(2, 'Da Sạch- Khỏe Là Tiền Đề Của Làn Da Không Mụn', 'Da Sạch- Khỏe Là Tiền Đề Của Làn Da Không Mụn', '<span style="font-size:14px"><strong>HANA BEAUTY - CHUYÊN MỸ PHẨM XÁCH TAY</strong></span><br />\r\n<span style="font-size:14px">Tết sắp đến gần bạn quan tâm đến việc DƯỠNG DA để da mịn màng mướt khoẻ hay là MAKEUP để mang đến hiệu ứng da đẹp 1 cách nhanh nhất ạ ?<br />\r\n-----&gt; Nghe có vẻ là 2 khái niệm riêng biệt nhưng thật ra thì cả hai lại liên quan đến nhau, vì da đẹp là tiền đề của lớp makeup đẹp<br />\r\nKhi da được chăm sóc đúng cách và dưỡng ẩm đủ thì da sẽ trở nên căng mướt. Và tất nhiên lớp makeup chỉ đẹp và tự nhiên nhất khi da thật sự đủ độ ẩm khoẻ và ít khuyết điểm khi đó chỉ cần một lớp#Cushion hoặc kem nền với độ che phủ mỏng nhẹ thì chắc chắn bạn đã đủ tươi tắn với vẻ ngoài tự nhiên của mình, tự tin trong buổi họp lớp ngồi 8 chuyện xưa cùng đám bạn cũ . Nguyên năm gặp để check in 1 lần&nbsp;</span><br />\r\n<span style="font-size:14px"><strong>TẢN MẠN CHUYỆN NĂM XƯA</strong></span><br />\r\n<span style="font-size:14px">Nhớ tầm này năm ngoái da mình vẫn trong tình trạng da sần sùi khô và sạm, thiệt sự là lo lắng cho cái Tết sắp đến gần và mình đã được biết đến loại mặt nạ #mybeautydiary này qua 1 chị beauty blogger : chị #banhbeophuphiem và rất nhiều feedback tốt đẹp từ các chị em đã dùng qua. Nó như 1 cuộc cách mạng mang lại hiểu quả nhanh ngoài sức mong đợi</span>\r\n<div style="text-align: center;"><img alt="" src="/file/ckfinder/userfiles/images/15894411_1637198409917755_474428940902961389_n.jpg" style="height:800px; width:600px" /></div>\r\n<span style="font-size:14px">---&gt; @uyết định dùng thử .kết quả là 1 tuần đắp 2 lần trong vòng 1 tháng da đã thay đổi 1 cách không thể ngờ được luôn đó&nbsp;<br />\r\nDa đủ độ ẩm nên mướt lắm - kiểu da pưng pưng ngậm nước í, còn khi makeup kiểu ăn phấn thôi rồi chứ không bị cakey ( da ra đằng da - phấn ra đằng phấn)&nbsp;<br />\r\nDa sáng hẳn ra - trong giới skincare ở nước ngoài người ta không có khái niệm về sản phẩm làm trắng thay vào đó là dùng những sản phẩm giúp da sáng khoẻ và đều màu trong đó có thể kể đến là serum dòng vitamin C. Nhưng với mình lúc đó mặt nạ vẫn là lựa chọn nhanh gọn để chạy cho kịp cái tết sắp đến gần ^^</span><br />\r\n<span style="font-size:14px"><strong>DA MỀM MỊN VÀ KHÔNG BỊ SẦN SÙI</strong></span><br />\r\n<span style="font-size:14px">Tóm lại : Đây là mặt nạ thần thánh cứu cánh mình sau những ngày da thật sự xuống cấp do nhiều nguyên nhân - stress trong công việc hay thức khuya , thậm chí sau vài ngày bỏ bê skincare.</span>\r\n\r\n<div style="text-align: center;"><span style="font-size:14px"><img alt="" src="/file/ckfinder/userfiles/images/15966309_1637198656584397_3931164285883289336_n.jpg" style="height:800px; width:600px" /></span></div>\r\n<span style="font-size:14px">( Chắc ai cũng hiểu cảm giác có những ngày chẳng muốn làm gì chỉ muốn được lăn ngay ra ngủ sau khi về tới nhà )&nbsp;<br />\r\nHãy nhớ giùm Hana đây là 1 bước dưỡng sau tất cả các bước skincare hoàn chỉnh của bạn - đừng bao giờ nghĩ rằng nó thần thánh đến mức giúp bạn rạng rỡ khi bước làm sạch da của bạn không đúng cách : vd : srm với độ PH quá cao, tẩy trang chưa sạch sau khi trang điểm hoặc dùng kem chống nắng.<br />\r\nCó rất nhiều bạn thắc mắc rằng DA MỤN có nên dùng hay không , bạn phải hiểu rằng da mụn cũng cần được dưỡng ẩm nhưng thay vào đó skincare của bạn phải thêm bước trị mụn, đắp mặt nạ kháng khuẩn song song với mặt nạ thiên về dòng dưỡng này và Hana không khuyên dùng cho những bạn đang trong tình trạng viêm da nhiều mụn ạ&nbsp;<br />\r\nDa sạch- khoẻ là tiền đề của làn da không mụn<br />\r\nMột lần đắp mặt nạ hiểu quả gấp 6 lần xài serum vì tinh chất cô đặc thấm đẫm trong miếng mặt nạ<br />\r\nVậy nên hãy bắt đầu làm đẹp ngay hôm nay để kịp xúng xính đón tết nha.</span>', 'Da sạch- khỏe là tiền đề của làn da không mụn', 'Da sạch- khỏe là tiền đề của làn da không mụn', '', '', '', '', '', 1, '', 0, 0, '1484733003tri-mun-da-kho.jpg', 1, 0, 2),
(3, 'Massage với những viên đá này để da đẹp &quot;không tì vết&quot; yên tâm đón Tết', 'Massage với những viên đá này để da đẹp &quot;không tì vết&quot; yên tâm đón Tết\r\n', '<p><span style="color:rgb(0, 0, 0); font-family:roboto-regular,serif; font-size:14px"><strong><span style="font-size:16px"><span style="font-family:arial,helvetica,sans-serif">Chỉ với những nguyên liệu đơn giản, rẻ tiền bạn đã có thể tự làm mặt nạ chăm sóc da hiệu quả và an toàn.</span></span></strong></span><br />\r\n<span style="font-size:14px; text-align:justify">Khoa học đã chứng nhận rằng chúng ta <span style="color:#000000">nên&nbsp;</span></span><a href="http://bestie.vn/2016/09/nhung-dau-hieu-chung-to-ban-dang-rua-mat-sai-cach" style="font-size: 14px; text-align: justify; background-color: transparent; box-sizing: border-box; text-decoration: none; color: rgb(0, 51, 153) !important;"><span style="color:#000000">rửa mặt</span></a><span style="font-size:14px; text-align:justify"><span style="color:#000000">&nbsp;b</span>uổi sáng bằng những viên đá được làm từ nước các loại hoa quả thay cho nước lã. Những viên đá lạnh buốt này sẽ làm tăng tuần hoàn máu, trẻ hoá làn da, xóa đi những vết nhăn, ngăn chặn các dấu hiệu lão hóa thông thường và làm ửng hồng đôi má của bạn. Nó cũng giúp da sáng lên một cách tự nhiên hơn. Nên thực hiện 2 lần/tuần.</span></p>\r\n\r\n<p style="text-align: center;"><img alt="bestie cham soc da bang da vien " src="http://static1.bestie.vn/Mlog/ImageContent/201701/scrape-14646007708928-pimtha-hot-girl-thai-lan-6-6405-1464600623-20170107152308.jpg" style="border:0px; box-sizing:border-box; height:432px; vertical-align:middle; width:500px" /></p>\r\n\r\n<p><span style="font-size:14px">Nếu bạn phải vội ra khỏi nhà trong tình trạng mệt mỏi, kém tươi thì đây cũng là cách nhanh nhất khiến da mặt bạn trông mượt mà, mịn màng hơn và tươi tắn hơn.<br />\r\nBạn có thể kết hợp giữa việc làm đá với các loại nguyên liệu<span style="color:#000000">,&nbsp;</span><a href="http://bestie.vn/2016/11/nhung-my-pham-cac-nang-nen-so-huu-de-co-guong-mat-makeup-ma-nhu-khong" style="box-sizing: border-box; background-color: transparent; color: rgb(0, 51, 153) !important; text-decoration: none;"><span style="color:#000000">mỹ phẩm từ tự nhiên</span></a><span style="color:#000000">&nbsp;</span>như các loại lá hoặc quả khác nhau để có một chế độ chăm sóc hoàn hảo cho làn da. Tuy nhiên, nước lạnh từ vòi không nên dùng để làm đá rửa mặt. Sẽ tốt hơn nhiều nếu làm đá từ nước đun sôi hay nước khoáng không ga.</span><br />\r\n&nbsp;</p>\r\n<span style="font-size:14px"><strong>1.&nbsp;<span style="font-family:arial,helvetica,sans-serif"><span style="color:rgb(0, 0, 0)">Làm trắng da với đá viên nước cơm dùng cho da thường</span></span></strong></span>\r\n\r\n<div style="text-align: center;"><img alt="bestie cham soc da bang da vien 1" src="http://static1.bestie.vn/Mlog/ImageContent/201701/030-20170107145736.JPG" style="border:0px; box-sizing:border-box; height:563px; vertical-align:middle; width:500px" /></div>\r\n\r\n<p><span style="font-size:14px">Dùng nước cơm để làm đá, ngoài tác dụng dưỡng da, nó còn có tác<span style="color:#000000"> dụng&nbsp;</span><a href="http://bestie.vn/2016/12/da-trang-bat-tong-voi-nhung-phuong-phap-tam-trang-an-toan-tai-nha" style="box-sizing: border-box; background-color: transparent; color: rgb(0, 51, 153) !important; text-decoration: none;"><span style="color:#000000">làm trắng da</span></a><span style="color:#000000">. Nư</span>ớc cơm được chắt ra khi nấu cơm, để nguội rồi cho vào ngăn đá. Loại đá này nên giữ trong ngăn đá không quá 3 ngày.</span></p>\r\n<strong>​​​</strong><span style="font-size:14px"><strong>​​2.&nbsp;</strong></span><strong style="font-size:14px; text-align:justify"><span style="line-height:20.8px">Cung cấp độ ẩm cho da khô bằng đá viên hoa quả</span></strong>\r\n\r\n<div style="text-align: center;"><img alt="bestie cham soc da bang da vien 2" src="http://static1.bestie.vn/Mlog/ImageContent/201701/fruit-ice-cubes-20170107150123.jpg" style="border:0px; box-sizing:border-box; height:325px; vertical-align:middle; width:500px" /></div>\r\n\r\n<p><span style="font-size:14px">Da khô thích hợp với những loại đá được làm từ lá, hoa, cũng như quả. Những quả sơn tra đỏ thắm, những cọng bồ công anh là thứ nguyên liệu tuyệt diệu cho loại da này. Có thể làm đá từ nước quả, tốt nhất là những loại quả đỏ. Nghiền nát 3 thìa quả và khuấy đều trong 2 cốc nước sôi để nguội, sau đấy lọc lấy nước. Cho vào ngăn đá và dùng trong vòng 5 ngày.</span><br />\r\n<br />\r\n<strong>3.&nbsp;</strong><span style="font-size:14px"><strong>Se khít lỗ chân lông bằng đá viên nước hoa hồng</strong></span></p>\r\n\r\n<div style="text-align: center;"><img alt="bestie cham soc da bang da vien 3" src="http://static1.bestie.vn/Mlog/ImageContent/201701/our-white-rose-goji-blend-is-the-perfect-tea-to-drink-cold-try-our-iced-tea-recipe-today-love-tea-i-20170107150332.jpg" style="border:0px; box-sizing:border-box; height:500px; vertical-align:middle; width:500px" /></div>\r\n\r\n<p><span style="font-size:14px">Bạn có thể biến đổi một chú<span style="color:#000000">t cho&nbsp;</span><a href="http://bestie.vn/2016/11/3-loi-ich-that-bat-ngo-tu-glycerin-nuoc-hoa-hong-va-nuoc-cot-chanh" style="box-sizing: border-box; background-color: transparent; color: rgb(0, 51, 153) !important; text-decoration: none;"><span style="color:#000000">nước hoa hồng</span></a><span style="color:#000000">&nbsp;c</span>ủa mình bằng cách làm lạnh trong các khay đá. Những viên đá nước hoa hồng sẽ giúp cho da mặt được mềm mại và tươi mới hơn.<br />\r\n<br />\r\n<strong>4.&nbsp;</strong></span><span style="font-size:14px"><strong>Giải nhiệt, làm dịu làn da cháy nắng bằng đá viên nha đam</strong></span></p>\r\n\r\n<div style="text-align: center;"><img alt="bestie cham soc da bang da vien 4" src="http://static1.bestie.vn/Mlog/ImageContent/201701/aloe-vera-ice-cubes-final2-20170107150523.jpg" style="border:0px; box-sizing:border-box; height:341px; vertical-align:middle; width:500px" /></div>\r\n\r\n<p><span style="font-size:14px">Với hàm lượng nước cao, lá nha đam với cấu tạo đặc trưng có lớp gel trong suốt, mềm mịn và mát lành tinh khiết nên có công dụng tuyệt với với việc giải nhiệt và làm dịu làn da khi cháy nắng. Bạn chỉ cần cắt bỏ hai bên gai dọc thân lá, tách lấy phần gel bên trong, nghiền nát cùng với nước và dựng trong khay đá để lạnh. Sau khi ra ngoài dưới trời nắng nóng, hãy dùng một viên lướt nhẹ lên da, khi đó mọi tác động của nắng nóng và tia UV đều được giải quyết hết</span>.<br />\r\n<br />\r\n<strong>5.&nbsp;</strong><span style="font-size:14px"><strong>Làm sạch và sáng da với đá viên dưa leo,&nbsp;<span style="line-height:20.8px">mật ong và chanh</span></strong></span></p>\r\n\r\n<div style="text-align: center;"><img alt="bestie cham soc da bang da vien 5" src="http://static1.bestie.vn/Mlog/ImageContent/201701/cucumber-lemon-honey-ice-cubes-20170107150630.jpg" style="border:0px; box-sizing:border-box; height:700px; vertical-align:middle; width:500px" /></div>\r\n\r\n<p><span style="font-size:14px">Dưa leo chứa hàm lượng nước lên đến 90% giúp da luôn có đủ độ ẩm cần thiết, nên một khi kết hợp với các thành phần dưỡng ẩm của mật ong và khả năng kháng khuẩn chống viên của chanh tươi thì hiệu quả chăm sóc và làm sạch da sẽ trọn vẹn và sạch sâu hơn hẳn. Cách đơn giản nhất là bạn xay nhuyễn dưa leo cùng một thìa cafe mật ong rồi thêm vài giọt chanh tươi đựng trong khay đá và để lạnh. Mỗi khi sử dụng lấy một viên massage nhẹ nhàng trên da và để nguyên hỗn hợp trên da khoảng 5-10 phút để dưỡng chất thẩm thấu sâu trên da bạn.<br />\r\n<br />\r\n<strong>6.&nbsp;</strong></span><span style="font-size:14px"><strong>Trị mụn và vết thâm với đá viên Matcha</strong></span></p>\r\n\r\n<div style="text-align: center;"><img alt="bestie cham soc da bang da vien 6" src="http://static1.bestie.vn/Mlog/ImageContent/201701/making-veggie-ice-cubes-20170107150806.jpg" style="border:0px; box-sizing:border-box; height:375px; vertical-align:middle; width:500px" /></div>\r\n\r\n<p style="text-align:justify"><span style="font-size:14px">Trà xanh có tác dụng làm mát tại chỗ, trị mụn và chống lão hóa. Rất nhiều bạn nữ ưa thích chọn trà xanh để làm mặt nạ dưỡng da. Thay vì trộn với các nguyên liệu khác đắp mặt, bạn có thể pha bột trà và làm đông để tăng hiệu quả dưỡng da hơn nữa.<br />\r\nBạn hòa bột trà xanh với nước khoáng cho thật tan. Sau đó lọc qua rây để lấy phần nước trong. Dùng nước này cho vào khay đá và để đông. Hàng ngày dùng nó để rửa mặt sáng, tối bạn sẽ thấy da mặt trắng mịn, sạch mụn và đánh bay vết thâm đen.</span></p>\r\n\r\n<p><span style="font-size:16px"><span style="font-family:arial,helvetica,sans-serif"><strong><span style="color:rgb(0, 0, 0)">Cách chăm sóc da bằng đá viên</span></strong></span></span></p>\r\n\r\n<ul>\r\n	<li style="text-align: justify;"><span style="font-size:14px">Bước 1: Rửa mặt bằng nước ấm rồi dùng khăn mềm thấm khô da, lưu ý không nên dùng khăn mặt sần sùi và khô ráp tránh gây tổn hại da và tránh mụn lây lan nhiều hơn.</span></li>\r\n	<li style="text-align: justify;"><span style="font-size:14px">Bước 2: Cho 1 ít đá bào hoặc đá viên vào khăn khô, túm bốn góc thành 1 khối vuông.</span></li>\r\n	<li style="text-align: justify;"><span style="font-size:14px">Bước 3: Chườm đá trực tiếp lên vùng da bị mụn, để yên trong vài phút, sau đó chuyển sang khu vực khác. Đợi 10 phút sau đó thực hiện lại 1 lần nữa.</span></li>\r\n</ul>\r\n\r\n<p style="text-align:justify"><span style="font-size:14px">Bạn có thể thực hiện cách này đều đặn mỗi ngày trước khi đi ngủ hoặc sáng thức dậy, chỉ sau 1 tuần sẽ cảm nhận được tác dụng thực sự.<br />\r\n<span style="line-height:20.8px">&quot;Mọi thứ đều nở ra khi nóng lên và co lại khi lạnh đi&quot;. Đừng ngại sử dụng viên đá lạnh thoa lên mặt, kết hợp với các động tác massage mặt mỗi ngày, điều này sẽ giúp làm se khít lỗ chân lông trên da mặt, đồng thời giảm bớt việc các chất bã nhờn, giảm lượng bụi và vi khuẩn thâm nhập lỗ chân lông, giúp bạn làm sạch da mặt dễ dàng hơn mà không phải kỳ công chăm sóc.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'Massage với những viên đá này để da đẹp &quot;không tì vết&quot; yên tâm đón Tết', 'Massage với những viên đá này để da đẹp &quot;không tì vết&quot; yên tâm đón Tết', '', '', '', '', '', 1, '', 0, 0, '148488481820-01-2017 11-00-04 SA.png', 1, 1, 3),
(4, 'Bí quyết dưỡng da trắng hồng dành cho bạn gái', 'Bí quyết dưỡng da trắng hồng dành cho bạn gái ', '<div style="text-align: center;"><span style="font-size:16px"><span style="font-family:arial,helvetica,sans-serif"><strong>Bí quyết dưỡng da trắng hồng dành cho bạn gái</strong></span></span></div>\r\n\r\n<div style="text-align: justify;"><span style="font-size:14px"><span style="font-family:arial,helvetica,sans-serif">Làm sao để có được làn da trắng mịn hồng hào luôn là vấn đề được các bạn nữ quan tậm. Bạn đã biết cách dưỡng da trắng hồng chưa? Những bí quyết dưới đây sẽ giúp bạn!<br />\r\nChăm sóc da là một việc không dễ dàng, đòi hỏi bạn phải cẩn thận và kiên trì. Trên thực tế có rất nhiều biện pháp để giúp bạn sở hữu được nước da trắng hồng, mịn màng. Chú ý đến những vấn đề sau, bạn sẽ không còn lo lắng về làn da của mình nữa.<br />\r\nDưỡng da trắng hồng bằng cách làm sạch da mặt&nbsp;</span></span><br />\r\n<img alt="Dưỡng da trắng hồng" class="aligncenter wp-image-24288" src="http://www.phunu.news/wp-content/uploads/2016/06/D%C6%B0%E1%BB%A1ng-da-tr%E1%BA%AFng-h%E1%BB%93ng-2-433x290.jpg" style="background-color:rgb(255, 255, 255); border:0px; color:rgb(34, 34, 34); display:block; font-family:noto serif,serif; font-size:17.6px; height:auto; margin-bottom:24px; margin-left:auto; margin-right:auto; max-width:100%; text-align:center; width:473px" /><span style="font-size:14px"><span style="font-family:arial,helvetica,sans-serif">Điều đầu tiên trong quy trình dưỡng da trắng hồng đó là bạn phải luôn giữ cho da mặt sạch sẽ. Bạn phải rửa sạch tay, cho một lượng sữa rửa mặt vừa đủ vào lòng bàn tay, tạo bọt thật kỹ và xoa đều lên mặt. Chuyển động tròn, massage da mặt nhẹ nhàng khoảng 30 giây rồi dùng nước ấm rửa sạch mặt. Không nên dùng nước quá nóng vì như thế có thể sẽ làm tổn thương da mặt của bạn.<br />\r\nThường xuyên tẩy tế bào chết cho da<br />\r\nĐây là thao tác quan trọng để dưỡng da trắng hồng. Bạn có thể trộn đều đường và nước với tỉ lệ 2:1 để làm thành hỗn hợp tẩy tế bào chết cho da đơn giản tại nhà. Thoa đều lên ba phần của khuôn mặt: vùng trán và hai bên má. Nhẹ nhàng massage theo hình tròn khoảng 1 phút rồi rửa mặt với nước ấm.</span></span><br />\r\n<img alt="Dưỡng da trắng hồng" class="aligncenter wp-image-24289" src="http://www.phunu.news/wp-content/uploads/2016/06/D%C6%B0%E1%BB%A1ng-da-tr%E1%BA%AFng-h%E1%BB%93ng-3-421x290.jpg" style="background-color:rgb(255, 255, 255); border:0px; color:rgb(34, 34, 34); display:block; font-family:noto serif,serif; font-size:17.6px; height:auto; margin-bottom:24px; margin-left:auto; margin-right:auto; max-width:100%; text-align:center; width:449px" /><span style="font-size:16px"><span style="font-family:arial,helvetica,sans-serif"><strong>Dưỡng da trắng hồng bằng cách đắp mặt nạ dưỡng da</strong></span></span><br />\r\n<span style="font-size:14px"><span style="font-family:arial,helvetica,sans-serif">Công đoạn không thể thiếu để giúp bạn dưỡng da trắng hồng đó chính là thường xuyên đắp mặt nạ cho da.<br />\r\nBạn có thể dùng các loại nguyên liệu có sẵn ngay tại nhà để chăm sóc cho da như:<br />\r\n<strong><em>&ndash; Cà chua: </em></strong>Vitamin C và các loại dưỡng chất có trong cà chua sẽ giúp da bạn trắng mịn tự nhiên. Bạn có thể cắt cà chua thành nhiều lát mỏng rồi đắp lên mặt trong khoảng 20 phút, sau đó rửa mặt sạch bằng nước lạnh. Áp dụng cách này một tuần từ 2-3 lần, bạn sẽ sớm có được làn da trắng hồng.<br />\r\nBạn cũng có thể dùng nước ép cà chua trộn với vài giọt nước cốt chanh rồi xoa lên mặt trong vòng 15 phút. Thực hiện khoảng 1 tháng bạn sẽ thấy tác dụng diệu kỳ của cà chua đối với làn da của bạn.</span></span><br />\r\n<img alt="Dưỡng da trắng hồng" class="aligncenter wp-image-24290" src="http://www.phunu.news/wp-content/uploads/2016/06/D%C6%B0%E1%BB%A1ng-da-tr%E1%BA%AFng-h%E1%BB%93ng-4-435x290.jpg" style="background-color:rgb(255, 255, 255); border:0px; color:rgb(34, 34, 34); display:block; font-family:noto serif,serif; font-size:17.6px; height:auto; margin-bottom:24px; margin-left:auto; margin-right:auto; max-width:100%; text-align:center; width:473px" /><span style="font-size:14px"><span style="font-family:arial,helvetica,sans-serif"><em>&ndash;</em><strong><em><em> </em>Dưỡng da trắng hồng với dưa chuột</em></strong>: Dưa chuột là loại thực phẩm quen thuộc với các gia đình. Các axit tartaric và axit amin có trong dựa chuột có hiệu quả rất tốt để dưỡng trắng da. Dùng vài giọt nước cốt chanh trộn đều với nước ép dưa chuột rồi xoa lên mặt. Massage nhẹ nhàng kết hợp thư giãn khoảng 20 phút rồi rửa sạch mặt. Với cách làm này, làn da bạn sẽ mềm mịn, trắng hồng trông thấy. Thực hiện 2-3 lần trong tuần để thu được kết quả tốt nhất.<br />\r\n<em><strong>&ndash; Khoai tây:</strong></em> Một loại dưỡng chất từ thiên nhiên giúp dưỡng da trắng hồng không thể bỏ qua đó là khoai tây. Bạn chỉ cần hấp chín khoai tây sau đó nghiền nát và trộn đều với lượng sữa tươi sao cho tạo thành một hỗn hợp sền sệt. Thoa đều hỗn hợp này lên mặt, đợi trong khoảng 30 phút rồi rửa lại bằng nước ấm. Mỗi tuần thực hiện 2 lần, làn da của bạn sẽ trắng hồng rạng rỡ.</span></span><br />\r\n<br />\r\n<span style="font-size:16px"><span style="font-family:arial,helvetica,sans-serif"><strong>Thu nhỏ lỗ chân lông</strong></span></span><br />\r\n<span style="font-size:14px"><span style="font-family:arial,helvetica,sans-serif">Đây chính là hâu cuối cùng để bạn sở hữu được làn da trắng hồng, mịn màng. Luân phiên dùng nước ấm, lạnh để rửa mặt và cuối cùng là nước lạnh. Hãy tạt nước lên mặt thay vì rửa bằng tay. Dùng một viên đá nhỏ xoa nhẹ lên mặt để giúp thu nhỏ lỗ chân lông. Dùng khăn mềm lau khô và thoa kem dưỡng ẩm cho da.<br />\r\nVới những khâu chăm sóc như trên, tin chắc rằng bạn sẽ dưỡng da trắng hồng nhanh chóng và hiệu quả. Hãy nhớ che chắn cho da mỗi khi ra ngoài. Chúc các bạn may mắn!</span></span></div>\r\n\r\n<div style="text-align: justify;">&nbsp;</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n', 'Bí quyết dưỡng da trắng hồng dành cho bạn gái', 'Bí quyết dưỡng da trắng hồng dành cho bạn gái', '', '', '', '', '', 0, '', 0, 0, '1495620091download.jpg', 1, 0, 4);

